import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import Grid from "@material-ui/core/Grid";
import "./ContentImg.css";
// import Img from "../../image/filme1.jpg";
const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
  },
}));
const ContentImg = (props) => {
  const classes = useStyles();
  const val = Object.values(props);

  return (
    <div className={classes.root}>
      {val.map((repo) => {
        if (repo.type === "poster") {
          const vals = repo.configuration.items;
          console.log();
        }
        return (
          <Grid container spacing={1}>
            <Grid item xs={12} key={repo.id}>
              <a
                href={"https://filmnet.ir/contents/" + repo.configuration}
                className="content-img"
              >
                <img src={repo.configuration} alt={repo.display_title} />
              </a>
            </Grid>
          </Grid>
        );
      })}
    </div>
  );
};
export default ContentImg;
