import React, { useEffect, useState } from "react";
import ContentLoading from "./ContentLoading";
import ContentItems from "./ContentItems";
import InfiniteScroll from "react-infinite-scroller";
export default function App() {
  const ListLoading = ContentLoading(ContentItems);
  const [state, setState] = useState({
    loading: false,
    repos: null,
    nextURL: null,
  });

  //   const [loadMore, setLoadMore] = useState({
  //     loading: false,
  //     repos: null,
  //     nextURL: "",
  //   });

  function LoadMore(url) {
    useEffect(() => {
      getData();
    }, [setState]);
  }
  //   function InfiniteLis(url) {
  //     useEffect(() => {
  //       getData(url, setState);
  //     }, [setState]);
  //   }
  function getData() {
    const apiurl = "http://api-v2.filmnet.ir";
    const URL = apiurl + "/widgets/home-page?count=5";
    if (state.nextURL) {
      URL = apiurl + state.nextURL;
    }
    fetch(URL)
      .then((res) => res.json())
      .then((res) => {
        setState({
          loading: false,
          repos: res,
          nextURL: res.meta.next_url,
        });
      });
  }
  return (
    <InfiniteScroll
      pageStart={0}
      loadMore={this.loadItems.bind(this)}
      hasMore={this.state.hasMoreItems}
    >
      <ListLoading isLoading={state.loading} repos={state.repos} />
    </InfiniteScroll>
  );
}
