import React from 'react';
import { Fade } from 'react-slideshow-image';
import 'react-slideshow-image/dist/styles.css';
import './../../image/7424f63029db41bd9b35df817983c55e.jpg';
import './../../image/94511e5d24fd4c799f94f16a83d1842f.jpg';
import './../../image/816279b8cbb54ba4b2911b7b59a2cd91.jpg';

const fadeImages = [
    'https://static.filmnet.ir/images/94/51/94511e5d24fd4c799f94f16a83d1842f.jpg?w=1500',
    'https://static.filmnet.ir/images/74/24/7424f63029db41bd9b35df817983c55e.jpg?w=1500',
    'https://static.filmnet.ir/images/81/62/816279b8cbb54ba4b2911b7b59a2cd91.jpg?w=1500'
]
    const Slideshow = () => {
        return (
          <div className="slide-container">
            <Slide>
              <div className="each-slide">
                <div style={{'backgroundImage': `url(${slideImages[0]})`}}>
                  <span>Slide 1</span>
                </div>
              </div>
              <div className="each-slide">
                <div style={{'backgroundImage': `url(${slideImages[1]})`}}>
                  <span>Slide 2</span>
                </div>
              </div>
              <div className="each-slide">
                <div style={{'backgroundImage': `url(${slideImages[2]})`}}>
                  <span>Slide 3</span>
                </div>
              </div>
            </Slide>
          </div>
        )
    }
export default Slideshow;
