import React, { useEffect, useState } from "react";
import ContentLoading from "./ContentLoading";
import ContentItems from "./ContentItems";
import InfiniteScroll from "react-infinite-scroll-component";

const FetchItems = (props) => {
  const apiurl = "http://api-v2.filmnet.ir/widgets/home-page?offset=";
  const ListLoading = ContentLoading(ContentItems);
  const [state, setState] = useState([]);
  const [count, setCount] = useState(0);
  const [hasMore, setHasMore] = useState(true);
  useEffect(() => {
    getData(count);
  }, [count]);

  var i = 0;
  function getData(number) {
    i++;
    console.log(i);

    var URL = apiurl + number + "&count=5";
    fetch(URL)
      .then((res) => res.json())
      .then((res) => {
        if (res.meta.next_url) {
          setState((prev) => [...prev, res]);
        } else {
          setHasMore(false);
        }
      });
  }
  //  function hasLoadMore(){
  //   setCount(prev => prev+5);
  // }

  return (
    <InfiniteScroll
      dataLength={state.repos.length}
      // next={getData(count+5)}
      hasMore={hasMore}
      val={console.log(state.repos)}
      loader={<h4 className="">دریافت موارد بیشتر ...</h4>}
    >
      {/* {.map((item, index) => ( */}
      <ListLoading isLoading={state.isLoading} repos={state.repos} />
      {/* ))} */}
    </InfiniteScroll>
  );
};
export default FetchItems;
