import React,{useState} from "react";
import Button from "@material-ui/core/Button";
import Dialog from "@material-ui/core/Dialog";
import DialogActions from "@material-ui/core/DialogActions";
import DialogTitle from "@material-ui/core/DialogTitle";
import { Typography } from "@material-ui/core";

export default function FilterGrouping() {
  const [open, setOpen] = useState(false);

  const handleClickOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };

  return (
    <div>
      <Typography variant="p" component="span" className="filter-label">
        {" "}
        دسته بندی
      </Typography>
      <Button className="group-btn" onClick={handleClickOpen} >
        دسته بندی ها
      </Button>
      <Dialog
        open={open}
        onClose={handleClose}
        aria-labelledby="alert-dialog-title"
        aria-describedby="alert-dialog-description"
      >
        <DialogTitle id="alert-dialog-title">دسته بندی ها</DialogTitle>

        <DialogActions>
          <Button onClick={handleClose} color="primary">
            انصراف
          </Button>
          <Button onClick={handleClose} color="primary" autoFocus>
            اعمال فیلتر
          </Button>
        </DialogActions>
      </Dialog>
    </div>
  );
}
