import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import Paper from "@material-ui/core/Paper";
import Grid from "@material-ui/core/Grid";
import TwitterIcon from "@material-ui/icons/Twitter";
import InstagramIcon from "@material-ui/icons/Instagram";
import TelegramIcon from "@material-ui/icons/Telegram";
import FacebookIcon from "@material-ui/icons/Facebook";
import "./footer.css";
const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
  },
}));
const Footer = () => {
  const classes = useStyles();

  return (
    <div className={classes.root}>
      <Grid container spacing={3}>
        <Grid item xs={12}>
          <div className="footer-header">
            <h2>فیلمنت</h2>
          </div>
        </Grid>

        <Grid item xs={6}>
          <div className="footer-body">
            <a className href="/about">درباره ما</a>
            <a className href="/contact-us">ارتباط با ما</a>
            <a className href="/rules">قوانین</a>
            <a className href="/guide">راهنما</a>
            <p>تمامی خدمات و فعالیت های سامانه فیلم نت حسب مورد، به موجب مجوز نشر دیجیتال و تصدی رسانه بر خط به شماره 93/572 از وزارت فرهنگ و ارشاد اسلامی، تابع قوانین و مقررات جمهوری اسلامی ایران است.</p>
          </div>
        </Grid>
        <Grid item xs={6}>
          <Paper className={classes.paper}>xs=6</Paper>
        </Grid>
        <Grid item xs={3}>
          <Paper className={classes.paper}>xs=3</Paper>
        </Grid>
        <Grid item xs={12}>
          <div className="footer-social">
            <a href="/twitter" target="_blank" className="footer-social-item">
              <TwitterIcon />
            </a>
            <a href="/instagram" target="_blank" className="footer-social-item">
              <InstagramIcon />
            </a>
            <a href="/telegram" target="_blank" className="footer-social-item">
              <TelegramIcon />
            </a>
            <a href="/facebook" target="_blank" className="footer-social-item">
              <FacebookIcon />
            </a>
            <p>ما را دنبال کنید</p>
          </div>
        </Grid>
      </Grid>
    </div>
  );
};

export default Footer;
