import React, { useEffect, useState } from "react";
import ContentLoading from "./ContentLoading";
import ContentItems from "./ContentItems";
// import InfiniteScroll from "react-infinite-scroller";

const FetchContent = () => {
  // const ListLoading = ContentLoading(ContentItems);

  //   const [appState, setAppState] = useState({
  //     loading: false,
  //     repos: null,
  //     nextURL:null
  //   });
  const loadMore = ()=>{
    const apiUrl = `http://api-v2.filmnet.ir`;
    const ListLoading = ContentLoading(ContentItems);

    const [appState, setAppState] = useState({
      loading: false,
      repos: null,
      nextURL:null
    });
    useEffect(() => {
      setAppState({ loading: true });
      if(setAppState.nextURL)
      {
        fetch(apiUrl + setAppState.nextURL)
        .then((res) => res.json())
        .then((repos) => {
          setAppState({ loading: false, repos: repos, nextURL:repos.meta.next_url});
        });

      }
      else{
        const url = "/widgets/home-page?count=5";
        fetch(apiUrl + url)
          .then((res) => res.json())
          .then((repos) => {
            setAppState({ loading: false, repos: repos, nextURL:repos.meta.next_url});
          });
      }

    }, [setAppState]);
  }

  return <ListLoading isLoading={appState.loading} repos={appState.repos}/>;
};
export default FetchContent;
