import React, { useEffect, useState } from "react";
import ContentLoading from "./ContentLoading";
import ContentItems from "./ContentItems";
import InfiniteScroll from "react-infinite-scroll-component";

const FetchContents = () => {
  const ListLoading = ContentLoading(ContentItems);
  const [appState, setAppState] = useState({
    loading: false,
    repos: null,
    nextURL: "/widgets/home-page?count=5",
    hasMore: true,
  });
  // const [page, setPage] = useState({
  //   loading: false,
  //   repos: null,
  //   nextURL: null,
  // });
  function Fetch(x) {
    useEffect(() => {
      FetchContent(x);
    }, [setAppState]);
  }

  function FetchContent(nextUrl) {
    const apiUrl = "http://api-v2.filmnet.ir";
    const URL = apiUrl + nextUrl;
    setAppState({ loading: true });
    fetch(URL)
      .then((res) => res.json())
      .then((repos) => {
        setAppState({
          loading: false,
          repos: repos,
        });
        // setPage({
        //   loading: false,
        //   repos: repos,
        //   nextURL: repos.meta.next_url,
        // });
        if (!repos.meta.next_url) {
          setAppState({ hasMore: false });
        }
      });
  }
  function FetchMore(props) {

     Fetch(pro);
  }
  // function getUrl(url, url2) {
  //   if(url===url2){
  //     // console.log(url);
  //     return url;
  //   }
  // }
  return (
    <InfiniteScroll
      dataLength={window.length}
      hasMore={true}
      inverse={true}
      style={{ display: "flex", flexDirection: "column-reverse" }}
       next={FetchMore(appState)}
      key={Fetch(appState.nextURL)}
    >
      {}
      <ListLoading
        isLoading={appState.loading}
        repos={appState.repos}
        key={console.log(appState)}
      />
    </InfiniteScroll>
  );
};
export default FetchContents;
