import React, { Fragment } from "react";
import Button from "@material-ui/core/Button";
import ButtonGroup from "@material-ui/core/ButtonGroup";
import "./filter.css";
import { Typography } from "@material-ui/core";
export default function LabelBottomNavigation() {
  const [value, setValue] = React.useState("");

  const handleChange = (event, newValue) => {
    setValue(newValue);
  };
  return (
    <Fragment>
      <Typography variant="h6" component="h2" className="filter-label">
        {" "}
        نوع
      </Typography>
      <ButtonGroup
        size="large"
        color="primary"
        aria-label="large outlined primary button group"
        value={value}
        onChange={handleChange}
        className="filter-btns"
      >
        <Button className="filterBtn" label="فیلم" value="Film" />
        <Button className="filterBtn" label="سریال" value="Serie" />
        <Button className="filterBtn" label="مجموعه" value="Collection" />
      </ButtonGroup>
    </Fragment>
  );
}
