import React, { useEffect, useState } from "react";
import ContentLoading from "./ContentLoading";
import ContentItems from "./ContentItems";
import InfiniteScroll from "react-infinite-scroller";

const FetchContent = () => {
  const ListLoading = ContentLoading(ContentItems);
  const apiUrl = `http://api-v2.filmnet.ir`;
  const [appState, setAppState] = useState({
    loading: false,
    repos: null,
    hasMore: false,
    url: "/widgets/home-page?count=5",
  });
  const [moreState, setMoreState] = useState({
    loading: false,
    repos: null,
  });
  useEffect(() => {
    setAppState({ loading: true });

    fetch(apiUrl + appState.url)
      .then((res) => res.json())
      .then((repos) => {
        setAppState({ loading: false, repos: repos,url });
        console.log(repos);
      });
  }, [setAppState]);

  useEffect(() => {
    fetch(apiUrl + appState.repos.meta.next_url)
    .then((res) => res.json())
    .then((data) => {
      setMoreState({
        loading: false,
        data: data,
      });
      console.log(data);
    });
  },[setMoreState]);

  return (
    <div>
      <ListLoading isLoading={appState.loading} repos={appState.repos} />
      {/* {fetchMore} */}
      <ListLoading isLoading={moreState.loading} repos={moreState.data} />
    </div>
  );
};
export default FetchContent;
