import React, { useEffect, useState } from "react";
import ContentLoading from "./ContentLoading";
import ContentItems from "./ContentItems";
import InfiniteScroll from "react-infinite-scroller";

const FetchContent = (url) => {
  const ListLoading = ContentLoading(ContentItems);
  const apiUrl = `http://api-v2.filmnet.ir`;
  const [appState, setAppState] = useState({
    loading: false,
    repos: null,
    nextURL:null
  });

    useEffect(() => {
      if(setAppState.nextURL){
        setAppState({ loading: true });
        fetch(apiUrl + url)
          .then((res) => res.json())
          .then((repos) => {
            setAppState({ loading: false, repos: repos,nextURL:repos.meta.next_url });
          });
      }
      else{
        setAppState({ loading: true });

        fetch(apiUrl + url)
          .then((res) => res.json())
          .then((repos) => {
            setAppState({ loading: false, repos: repos,nextURL:repos.meta.next_url });
          });
      }

    }, [setAppState]);
    const FetchContent = () => {
  };

  return (
    <InfiniteScroll
    dataLength={appState.repos[0].length}
    next={FetchContent}
    hasMore={true}
  >
    {appState.repos[0].map(() => (
      <ListLoading
        key={appState.repos[0].id}
        isLoading={appState.loading}
        repos={appState.repos}
      />
    ))}
  </InfiniteScroll>
  );
};
export default FetchContent;
