import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import Grid from "@material-ui/core/Grid";
import "./ContentImg.css";
// import Img from "../../image/filme1.jpg";
const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
  },
}));
const ContentImg = (props) => {
  const classes = useStyles();
  const val = Object.values(props);

  // console.log(element);
  // const items = element[0];
  // const item = Object.values(items);
  // console.log(item);
  return (
    <div className={classes.root}>
      {val.map((repo) => {
        if (repo.type === "poster") {
          const item = Object.values(repo.configuration);
          const val = item[0];
          return (
            <Grid container spacing={1}>
              <Grid item xs={12} key={repo.id}>
                <a href={"https://filmnet.ir/contents/" +repo.configuration} className="content-img">
                  <img src={repo.configuration} alt={repo.display_title} />
                </a>
              </Grid>
            </Grid>
          );
        }
      })}
    </div>
  );
};
export default ContentImg;
