import React, { useEffect, useState } from "react";
import ContentLoading from "./ContentLoading";
import ContentItems from "./ContentItems";
import InfiniteScroll from "react-infinite-scroller";

export default function Test() {
  const apiurl = "http://api-v2.filmnet.ir/widgets/home-page?";
  const ListLoading = ContentLoading(ContentItems);
  const [state, setState] = useState({
    loading: false,
    repos: [],
    hasMoreItems: true,
  });
const [count,setCount]=useState(0);

  useEffect(() => {
    getData();
  }, [count]);

  var i = 0;
  function getData() {
    i++;
    console.log(i);
    var URL = apiurl + count + "&count=5";
    fetch(URL)
      .then((res) => res.json())
      .then((res) => {
        // console.log(res);
        if (res.meta.next_url) {
          setState({
            loading: false,
            repos: res,
          });
        } else {
          setState({
            hasMoreItems: false,
          });
        }
      });
  }
   function hasLoadMore(){
    setCount(prev => prev+5);
  }


  const loader = <div className="loader">دریافت موارد بیشتر ...</div>;
  return (
    <InfiniteScroll
      pageStart={0}
      loadMore={hasLoadMore}
      hasMore={state.hasMoreItems}
      val={console.log(state)}
      useWindow={true}
      loader={loader}
    >
     {state.repos.map((item, index) => (
      <ListLoading isLoading={state.isLoading} repos={item} />
      ))}
    </InfiniteScroll>
  );
}
